package com.example.administrator.postrecyclerview;

import android.app.Activity;
import android.os.Bundle;
import android.support.annotation.Nullable;
import android.support.v7.widget.DividerItemDecoration;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.view.View;
import android.view.inputmethod.InputMethodManager;
import android.widget.Button;

import java.util.ArrayList;

/**
 * Created by Administrator on 2017-07-01.
 */

public class ReplyActivity extends Activity {

    InputMethodManager inputMethodManager;

    Button reply_back_btn;
    RecyclerView reply_recyclerView;

    ArrayList<ReplyData> data_list;
    LinearLayoutManager linear_layout_manager;

    ReplyRecyclerAdapter adapter;

    @Override
    protected void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_reply);

        reply_back_btn = (Button)findViewById(R.id.reply_back_btn);

        inputMethodManager = (InputMethodManager)getSystemService(INPUT_METHOD_SERVICE);

        reply_recyclerView = (RecyclerView)findViewById(R.id.reply_recyclerview);
        reply_recyclerView.setHasFixedSize(true);
        reply_recyclerView.addItemDecoration(new DividerItemDecoration(getApplicationContext(),DividerItemDecoration.VERTICAL));

        data_list = new ArrayList<>();
        data_list.add(new ReplyData("data:image/jpeg;base64,","Jiyong","리플입니다","10분전"));
        data_list.add(new ReplyData("data:image/jpeg;base64,","Jiyong","리플입니다","10분전"));
        data_list.add(new ReplyData("data:image/jpeg;base64,","Jiyong","리플입니다","10분전"));
        data_list.add(new ReplyData("data:image/jpeg;base64,","Jiyong","리플입니다","10분전"));

        adapter = new ReplyRecyclerAdapter(data_list, click_listener);
        linear_layout_manager = new LinearLayoutManager(getApplicationContext(), LinearLayoutManager.VERTICAL,false);

        reply_recyclerView.setAdapter(adapter);
        reply_recyclerView.setLayoutManager(linear_layout_manager);


    }

    void selectReply(final int position){}

    public View.OnClickListener click_listener = new View.OnClickListener(){
        @Override
        public void onClick(View v) {
            final int position = reply_recyclerView.getChildAdapterPosition(v);
            selectReply(position);
        }
    };
}
